$(document).ready((function(){var video=document.getElementById("video-artio"),pause=!1,start_video,end_video;$(".video-buttons button").click((function(){start_video=$(this).data("time"),end_video=$(this).data("end"),pause=!0,video.currentTime=start_video,video.play()})),video.ontimeupdate=function(){if(pause){var current=Math.floor(video.currentTime);current<=end_video&&current>=start_video?current==end_video&&(video.pause(),pause=!1):pause=!1}}}));